    public int minCost(int[] houses, int[][] cost, int m, int n, int t) {
//        int[] maxTarget = new int[m];
//
//        for (int i = 0; i < m; i++) {
//            maxTarget[i] = i + 1;
//        }

        for (int i = 0; i < m; i++) {
            houses[i]--;
        }


//        int current = t;
//        int currentHouse = -1;
//        for (int i = m - 1; i >= 0; i--) {
//            if (houses[i] != 0) {
//                if (currentHouse == -1 || currentHouse == houses[i]) {
//                    currentHouse = houses[i];
//                    maxTarget[i] = Math.min(current, maxTarget[i]);
//                }
//                else {
//                    currentHouse = houses[i];
//                    current--;
//                    maxTarget[i] = Math.min(current, maxTarget[i]);
//                }
//            } else {
//              maxTarget[i] = Math.min(current, maxTarget[i]);
//            }
//        }
//
//        if (maxTarget[0] <= 0)
//            return -1;

        int[][][] minCost = new int[m][n][t + 1];
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                for (int k = 0; k <= t; k++) {
                    minCost[i][j][k] = 1_000_001;
                }
            }
        }

        if (houses[0] != -1)
            minCost[0][houses[0]][1] = 0;
        else
            for (int i = 0; i < n; i++)
                minCost[0][i][1] = cost[0][i];

        for (int i = 1; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (houses[i] == - 1 || houses[i] == j) {
                    for (int k = 1; k <= t; k++) {
                        for (int l = 0; l < n; l++) {
                            if (l == j)
                                minCost[i][j][k] = Math.min(minCost[i][j][k], minCost[i - 1][j][k] + cost[i][j]);
                            else
                                minCost[i][j][k] = Math.min(minCost[i][j][k], minCost[i - 1][l][k - 1] + cost[i][j]);
                        }
                        if (houses[i] != - 1 && minCost[i][j][k] != 1_000_001)
                            minCost[i][j][k] -= cost[i][j];
                    }
                }

            }
        }
        int result = 1_000_001;
        for (int i = 0; i < n; i++) {
            result = Math.min(result, minCost[m - 1][i][t]);
        }
        return result == 1_000_001 ? -1 : result;
    }

